CREATE TABLE cuenta (
    cuenta_id	INT AUTO_INCREMENT PRIMARY KEY,
    contraseña	VARCHAR(15),
    correo	VARCHAR(450),
    apellido	VARCHAR(60),
    nombres	VARCHAR(30),
    sexo	CHAR(1),
    codigo_postal	CHAR(5),
    num_telefono	CHAR(10),
    ciudad	VARCHAR(30),
    pais	VARCHAR(20),
    fecha_nacimiento	DATE,
    puntos	INT,
    nivel_id	INT,
	numero_tarjeta	INT,
    FOREIGN KEY (nivel_id) REFERENCES niveles(nivel_id)
	FOREIGN KEY (numero_tarjeta) REFERENCES tarjeta(numero_tarjeta)
);

