CREATE TABLE cuentas (
    cuenta_id	INT AUTO_INCREMENT PRIMARY KEY,
    contraseña	VARCHAR(10),
    correo	VARCHAR(450),
    apellido	VARCHAR(60),
    nombres	VARCHAR(30),
    sexo	CHAR(1),
    codigo_postal	CHAR(5),
    num_telefono	INT,
    ciudad	VARCHAR(30),
    pais	VARCHAR(20),
    fecha_nacimiento	DATE,
    puntos	INT,
    nivel_id	INT,
    FOREIGN KEY (nivel_id) REFERENCES niveles(nivel_id)
);

